#[cfg(feature = "test-util")]
#[cfg(test)]
mod test {
    use crate::{
        api::gen::bitable::create_bitable_table::{
            CreateBitableTableReq, CreateBitableTableResp, CreateBitableTableRespInner,
        },
        core::{model::CommonResponse, Lark},
    };

    #[tokio::test]
    async fn test_mock() {
        let lark = Lark::new("".to_owned(), "".into());
        let mocker = lark
            .bitable()
            .mock()
            .mock_create_bitable_table(|_| {
                Ok((CreateBitableTableResp::default(), CommonResponse::default()))
            })
            .build();
        let res = lark
            .bitable()
            .create_bitable_table(CreateBitableTableReq::default())
            .await;
        assert!(res.is_ok());
        mocker.clear();
        let res = lark
            .bitable()
            .create_bitable_table(CreateBitableTableReq::default())
            .await;
        assert!(res.is_err());
    }

    const REQ: &str = r#"{
    "table":{
        "name":"数据表名称",
        "default_view_name":"默认的表格视图",
        "fields":[
            {
                "field_name":"多行文本",
                "type":1
            }
        ]
    }
}"#;

    #[test]
    fn test_req() {
        if REQ == "{}" {
            return;
        }
        if let Err(e) = serde_json::from_str::<super::CreateBitableTableReqBody>(REQ) {
            panic!("{}", e);
        }
    }

    const RESP: &str = r#"{
	"code": 0,
	"msg": "success",
	"data": {
		"table_id": "tblDBTWm6Es84d8c",
		"default_view_id": "vewUuKOz2R",
		"field_id_list": [
			"fldhr2hBEA"
		]
	}
}"#;
    #[test]
    fn test_resp() {
        let res = serde_json::from_str::<CreateBitableTableRespInner>(RESP);
        if let Err(e) = res {
            panic!("{}", e);
        }
        if let Ok(v) = serde_json::from_str::<serde_json::Value>(RESP) {
            if v.get("data").is_some() {
                assert!(res.unwrap().data.is_some());
            }
        }
    }
}
