    #[tokio::test]
    async fn test_mock() {
        let lark = Lark::new("".to_owned(), "".into());
        let mocker = lark
            .lingo()
            .mock()
            .mock_search_lingo_entity(|_| {
                Ok((SearchLingoEntityResp::default(), CommonResponse::default()))
            })
            .build();
        let res = lark
            .lingo()
            .search_lingo_entity(SearchLingoEntityReq::default())
            .await;
        assert!(res.is_ok());
        mocker.clear();
        let res = lark
            .lingo()
            .search_lingo_entity(SearchLingoEntityReq::default())
            .await;
        assert!(res.is_err());
    }

